import axios from "axios";
import React, {useState, useEffect} from "react";
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import { Link, useNavigate } from "react-router-dom";
import { getuserservice } from "../services/user";

function Loginhere(){

    const [obj,updateobj]=useState({lemail:"email@gmail",lpwd:"gmail"});
    var navigate=useNavigate();
  
    

    const update=(event)=>{
      var {name,value}=event.target;
        updateobj({...obj,[name]:value});
    }

    const loginwithquery=async()=>{
      var url="http://localhost:2002/user/login-with-query";
      var resp=await axios.post(url,{
        email:obj.lemail,
        pwd:obj.lpwd
      });

      if(resp.data.status!==false)
      { 
        localStorage.setItem("token",resp.data.token);
        localStorage.setItem("a_email",resp.data.user.email);


         if(resp.data.user.type==="Client")
         {
          navigate("/cdash");
         }
         else
         navigate("/pdash");
      }
      else
      {
        alert(resp.data.message);
      }
    } 
    
    var token= localStorage.getItem('token');

    const getUser= async()=>{ 

        try{
            // const resp= await axios.get('http://localhost:2002/user/currentuser',
            // {
            //     headers:{
            //         'Authorization':token
            //     }
            // });

            const resp= await getuserservice();
            alert("hlo"+resp);

            if(resp.data.status){
              if(resp.data.user.type==="Client")
              {
               navigate("/cdash");
              }
              else
              {
                console.log("service-Provider");
                navigate("/pdash");
              }
           }
           else
           {
                alert(resp.data.message);
           }
        }
        catch(err){
            console.log(err);
        }
    }

    useEffect(()=>{
        if(token)
        {
          getUser();
        }
    },[])


    return(
      <div>
                <div>

                  
                <center>
                 <h1>Login Here....</h1>
    
                 <Form method="get"  action="">
                  <Form.Group as={Row} className="mb-3" controlId="formHorizontalEmail">
                    <Form.Label column sm={2}>
                      Email
                    </Form.Label>
                    <Col sm={5}>
                      <Form.Control type="email" placeholder="Email" value={obj.lemail} name="lemail" onChange={update}/>
                    </Col>
                  </Form.Group>
            
                  <Form.Group as={Row} className="mb-3" controlId="formHorizontalPassword">
                    <Form.Label column sm={2}>
                      Password
                    </Form.Label>
                    <Col sm={5}>
                      <Form.Control type="password" placeholder="Password" value={obj.lpwd} name="lpwd" onChange={update}/>
                    </Col>
                  </Form.Group>

                  <Link to="/signup">
                    <h4>Signup</h4>
                  </Link>
            
                  <Form.Group as={Row} className="mb-3">
                    <Col sm={{ span: 5, offset: 2 }}>
                      <Button type="button" onClick={loginwithquery}>Login</Button>
                    </Col>
                  </Form.Group>
                 
                </Form>
    
    
                </center>
            </div>
      </div>
        
    )
}

export default Loginhere;
