import React ,{ useState,useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Row from 'react-bootstrap/Row';
import Container from 'react-bootstrap/esm/Container';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Image from 'react-bootstrap/Image';
import ListingCard from '../common\'/ListingCard';



function Save() {

  const ae=localStorage.getItem("a_email");
  var navigate=useNavigate();

  const [data,updatedata]=useState([]);

  const [obj,updateobj]=useState({city:""});

  const [category,updatecat]=useState([]);

  const [validated, setValidated] = useState(false);

  const update=(event)=>{
    var {name,value}=event.target;

    updateobj({...obj,[name]:value});
}


    async function fetchcity(){
        var url="http://localhost:2002/provider/fetchdetails-client";
   
    var response = await axios.post(url);

     updatecat(response.data.cat); 
         
    }

    async function fetch(){
      var url="http://localhost:2002/provider/fetchdetails-client-post";

      var response = await axios.post(url,obj);
      alert(JSON.stringify(response));
      updatedata(response.data.user);
    }
 
    useEffect(()=>
        {
            fetchcity();
        },[]
    )

    
  return (
    <div>
        <div>
            <br />
            <h1>Provider profile</h1>
            <br />
            <br />
        </div>
    <Container>
    <Form noValidate validated={validated}>
      

      <Row className="mb-3">
        <Form.Group as={Col} md="4" controlId="validationCustom03">
          <Form.Label>Category</Form.Label>
            <Form.Select aria-label="Default select example" required 
          name='category'
          value={obj.category}
          onChange={update}>
              {
                category.map((str)=><option value={str}>{str}</option>)
              }
          </Form.Select>
          <Form.Control.Feedback type="invalid">
            Please provide a valid category.
          </Form.Control.Feedback> 
        </Form.Group> 
      </Row>

      
      <button type="button" onClick={fetch} >Post</button>
    </Form>
    </Container>
    <p> 
        {JSON.stringify(obj)}  
    </p>

    <div className="search-results-section">
            <ul className="trip-items">
                {data.length === 0 ? (<h4>No results found</h4>) : (data?.map((location) => {
                    return (<li key={location._id} className="trip-item">
                        <ListingCard location={location}/>
                    </li>)
                }))

                }
            </ul>
        </div>
    {/* 
    <p> 
        {JSON.stringify(city)}  
    </p>
    <p> 
        {JSON.stringify(category)}  
    </p> */}
    </div>
  );
}

export default Save;
